import React, { PureComponent } from 'react'
import { dataService } from '../services/dataService';
import '../myStyle.css'
var jwt = require('jsonwebtoken');

class uploadFile extends PureComponent {
    constructor(props) {
        super(props)
        this.state = {
            description: "",
            file: [],
            res: ""
        }
        this.upload = this.upload.bind(this);
    }

    upload() {
        const userData = this.props.user
        console.log(`userData: ${userData}`);
        const file = this.state.file;
        console.log("file name ",file[0]);
        if (file.length > 0) {
            dataService.uploadFile(file[0], userData, this.state.description)
                .then(json => {
                    console.log(json);
                    this.setState({
                        res: "File Uploaded successfully"
                    });
                    setTimeout(()=> {
                        if (this.props.isAdmin){
                            this.props.refreshList();
                        } else {
                            this.props.refreshList2(userData);
                        }
                    }, 500);
                })
                .catch(reason => {
                    console.log(reason);
                    this.props.refreshList();
                });
        }
    }

    render() {
        return (
            <div>
                    <p> File Upload: {this.state.res} </p>
                        <input class='other_buttons' type="file" onChange={e => this.setState({
                            file: e.target.file
                        })}> 
                        </input>
                       <a>
                       <input
                            value={this.state.desc}
                            onChange={e => this.setState({
                                description: e.target.value
                            })}
                            placeholder="descriptioniption"
                            type="text"
                            name="descriptioniption"
                        />
                        &nbsp; &nbsp;
                        <button class='other_buttons' onClick={this.upload}>Upload</button>
                       </a>

            </div>
        )
    }
}

export default uploadFile
