import React, { PureComponent } from 'react'
import { dataService } from '../services/dataService';
import '../myStyle.css'
var jwt = require('jsonwebtoken');

class FileUpload extends PureComponent {
    constructor(props) {
        super(props)
        this.state = {
            file: [],
            descr: "",
            result: ""
        }
        this.uploadFile = this.uploadFile.bind(this);
    }

    uploadFile() {
        const userData = this.props.user
        console.log(`userData: ${userData}`);
        const file = this.state.file;
        console.log("file name ",file[0]);
        if (file.length > 0) {
            dataService.uploadFile(file[0], userData, this.state.descr)
                .then(json => {
                    console.log(json);
                    this.setState({
                        result: "File Uploaded successfully"
                    });
                    setTimeout(()=> {
                        if (this.props.isAdmin){
                            this.props.refreshList();
                        } else {
                            this.props.refreshList2(userData);
                        }
                    }, 500);
                })
                .catch(reason => {
                    console.log(reason);
                    this.props.refreshList();
                });
        }
    }

    render() {
        return (
            <div>
                    <p> File Upload Result: {this.state.result} </p>
                        <input class='other_buttons' type="file" onChange={e => this.setState({
                            file: e.target.file
                        })}>
                        </input>
                       <a>
                       <input
                            value={this.state.desc}
                            onChange={e => this.setState({
                                descr: e.target.value
                            })}
                            placeholder="Description"
                            type="text"
                            name="Description"
                        />
                        &nbsp; &nbsp;
                        <button class='other_buttons' onClick={this.uploadFile}>Upload</button>
                       </a>

            </div>
        )
    }
}

export default FileUpload
