import React, { PureComponent } from 'react'
import { Button, Navbar, Card } from "react-bootstrap";
import { useRouteMatch } from 'react-router-dom';

// rfc
class LogInPage extends PureComponent {
// function LogInPage() {
//     let { path, url, location } = useRouteMatch();
//     console.log(location);
    constructor(props) {
        super(props)

        this.state = {
            email: "",
            password: "",
            data: new FormData()
        }
    }
    login = () => {
        const email = this.state.email;
        const pass = this.state.password;
        this.data.append('email', email);
        this.data.append('password',pass);
        alert("in login ");
        console.log(email," ",pass);
        const requestOptions = {
                                method: 'POST',
                                body: this.data
                 };
              const r = fetch("http://localhost:8080/login", requestOptions)
              . then(response => response. json())
              . then(data => {
              console.log(data);
              if(data === "Please signup"){
                alert("pls signup");
                return <Redirect to='./signin' />
              }
              });
    }

    render(){
    return (
        <div>
            <Navbar bg="dark" variant="dark">
                <Navbar.Brand>DropBucket</Navbar.Brand>
            </Navbar>

            <Card style={{ width: '18rem', margin: "100px", justifyContent: "center"}}>
                <Card.Header>Welcome to the DropBucket App</Card.Header>
                <Card.Body>
                    <Card.Text></Card.Text>
                    EMAIL: <input value={this.state.email}
                     onChange={e => this.setState({
                     email: e.target.value
                      })}
                       placeholder="Email"
                       type="text"
                       name="Description"/>
                       PASSWORD: <input value={this.state.password}
                                            onChange={e => this.setState({
                                            password: e.target.value
                                             })}
                                              placeholder="PASSWORD"
                                              type="password"
                                              name="Description"/>
                    <Button  variant="primary" onClick={this.login}>
                        LogIn / SignUp   </Button>
                </Card.Body>
            </Card>

        </div>

    );
    }
// }
}

export default LogInPage
