//import axios from "axios";
//import * as AWS from 'aws-sdk/global';

global.fetch = require('node-fetch');
var AmazonCognitoIdentity = require('amazon-cognito-identity-js');

export const dataService = {
    getUserData,
    getAdminData,
    uploadFile,
    deleteFile,
    getUser,

}

export const apiConfig = {
    endpointURL: "http://127.0.0.1:8080"
}

function getUserData(userName) {
    console.log("user"+userName);
    const requestOption = {
//    mode: 'no-cors'
        method: 'GET',
        headers: { "Content-Type": "application/json" },

    }
    return fetch(`${apiConfig.endpointURL}/user_data/${userName}`, requestOption).then(res => {
        console.log("response : ",res);
        return res.json();
    })
}

function getAdminData() {

    const requestOption = {
        method: 'GET',
        headers: { "Content-Type": "application/json" }
    }
    return fetch(`${apiConfig.endpointURL}/getAdminData`, requestOption).then(res => {
        console.log(res); 
        return res.json();
    })
}

function uploadFile(inputFile, userData, description) {
    const formData = new FormData();
    formData.append('file', inputFile);
    formData.append('first_name', "aa");
    formData.append('last_name', "bb");
    formData.append('desc', description);
    formData.append('email', userData);
    console.log(`UserNAme: ${userData}, desc: ${description}`);
    const requestOption = {
        method: 'POST',
        body: formData,
       // headers: { "Content-Type": inputFile.type }
    }
    return fetch(`${apiConfig.endpointURL}/upload`, requestOption).then(res => {
      console.log("res ", res);
        return res;
    })
}
function deleteFile(fileName,id) {
    const formData = new FormData();
    formData.append('deleteFile', fileName);
    formData.append('userId', "id");
    const requestOption = {
        method: 'DELETE',
        headers: { "Content-Type": "application/json" }
    }
    return fetch(`${apiConfig.endpointURL}/delete_files/${id}`, requestOption)
}


function getUser() {
    var poolData = {
        UserPoolId: 'us-east-2_0064KOcCQ',
        ClientId: '2ub6k6peilk5te5prhkq2cjm5k',
    };
    var userPool = new AmazonCognitoIdentity.CognitoUserPool(poolData);
    var cognitoUser = userPool.getCurrentUser();

    console.log("cognito user", cognitoUser);

    if (cognitoUser != null) {
        cognitoUser.getSession(function(err, session) {
            if (err) {
                alert(err.message || JSON.stringify(err));
                return;
            }
            console.log('session validity: ' + session.isValid());

            // NOTE: getSession must be called to authenticate user before calling getUserAttributes
            cognitoUser.getUserAttributes(function(err, attributes) {
                if (err) {
                    // Handle error
                    console.log(err);
                } else {
                    // Do something with attributes
                    console.log(attributes);
                }
            });

