import React, { PureComponent } from 'react'
import { CognitoJwtVerifier } from "aws-jwt-verify";
import { CognitoUserPool } from "amazon-cognito-identity-js";
import { saveAs } from "file-saver";
import { Button, Navbar, Table } from "react-bootstrap";
import { dataService } from '../services/dataService';
import FileUpload from './FileUpload';
import LogOut from './LogOut';
import LogInPage from './LogInPage';
var jwt = require('jsonwebtoken');


class UserPage extends PureComponent {




    constructor(props) {
        super(props)

        const sessionToken = sessionStorage.getItem("token")
//         const verifier = CognitoJwtVerifier.create({
//           userPoolId: "us-east-2_mO6A60V6L>",
//           tokenUse: "access",
//           clientId: "5rvr724i7hngpqmmivbdo35gum",
//         });

        // JWT DECODE
        // Store decoded jwt in session storage

        this.state = {
            userDataDynamo: [],
            userData: undefined,
            desc: "",
            isAdmin: false
        }
        this.setDescription = this.setDescription.bind(this)
        this.updateTable = this.updateTable.bind(this)
    }

    setDescription(d) {
        this.setState({
            desc: d
        })
    }

    updateTable2(user,user_admin) {
        console.log("Called Update Table");
        var boll = false;
        if(user_admin == true)
            boll = true;
        dataService.getUserData(user,boll)
            .then(json => {
                console.log(json);
                if (Array.isArray(json)) {
                    this.setState({
                        userDataDynamo: json
                    });
                }
            })
            .catch(reason => {
                console.log("Failed to fetch data from server, reason is : ", reason);
            });
    }
    updateTable(user) {
        console.log("Called Update Table");
        dataService.getUserData(user,true)
            .then(json => {
                console.log(json);
                if (Array.isArray(json)) {
                    this.setState({
                        userDataDynamo: json
                    });
                }
            })
            .catch(reason => {
                console.log("Failed to fetch data from server, reason is : ", reason);
            });
    }

//     async getUserInfo() {
//       const user = await Auth.currentAuthenticatedUser();
//       console.log('attributes:', user.attributes);
//     }

    componentDidMount() {
//         this.getUserInfo();

        var token = sessionStorage.getItem("token");
        console.log("token ",token);
        try {
          const payload = this.verifier.verify(
            token
          );
          console.log("Token is valid. Payload:", payload);
        } catch {
          console.log("Token not valid!");
        }
        var decoded = jwt.decode(token);
        // get the decoded payload and header
        var decoded = jwt.decode(token, { complete: true });
        console.log("decoded header: ",decoded.header);
        console.log("payload: ",decoded.payload);
        const userObj = decoded.payload;
        this.setState({
            userData: userObj
        })
        const isAdmin = userObj && userObj["cognito:groups"] && userObj["cognito:groups"].filter(g => g == "admin").length > 0;
//            const admin = false;
//            if(this.state.userData.email=="2712aabbccdd@gmail.com"){
//             admin = true;
//            }
        this.setState({ isAdmin });
        setTimeout(()=> {
            console.log("UserEmailCheckBefore:"+this.state.userData.email)
            console.log("first name: ",this.state.userData.first_name)
            console.log("first name: ",this.state.userData.last_name)
            console.log("isAdmin:"+this.state.isAdmin)
            if(this.state.userData.email=="2712aabbccdd@gmail.com"){
                this.state.isAdmin = true;
            }
            if (this.state.isAdmin){
                this.updateTable(this.state.userData.email,this.state.isAdmin)
            } else {
                this.updateTable2(this.state.userData.email,this.state.isAdmin);
            }
        }, 500);


        dataService.getUser()
    }


    onClickDownLoad(file) {
        console.log("file name:"  ,file);
        window.open("https://dfbiaa0nywvvp.cloudfront.net/" + file);
        saveAs("https://dfbiaa0nywvvp.cloudfront.net/"+file, file );
    }

    onClickDelete(fileName, id) {
        dataService.deleteFile(fileName, id)
            .then(json => {
                console.log(json);
                setTimeout(()=> {
                if (this.state.isAdmin){
                    this.updateTable()
                } else {
                    this.updateTable2(this.state.userData.email);
                }
            }, 300);

            })
            .catch(reason => {
                console.log("Failed to delete, reason is : ", reason);
            });
    }

    // fetchListAgain




    render() {

        const { isAdmin } = this.state;

        return (

            <div>
                <Navbar bg="dark" variant="dark">
                    <Navbar.Brand>DropBucket</Navbar.Brand>
                    <Navbar.Collapse className="justify-content-end">
                        <Navbar.Text>
                            Signed in as: {this.state.userData &&
                                <a href="#login">{this.state.userData.email}</a>}
                            &nbsp;&nbsp;
                            User Type: {
                                isAdmin && <a> "Administrator" </a>
                            }
                            {
                                !isAdmin && <a> "User (Non Admin)"</a>
                            }
                        </Navbar.Text>
                    </Navbar.Collapse>
                    {this.state.userData && <LogOut></LogOut>}
                    {!this.state.userData && <LogInPage></LogInPage>}
                </Navbar>
                {
                    this.state.userData &&
                    <FileUpload
                        user={this.state.userData.email}
                        desc={this.state.desc}
                        refreshList={e => this.updateTable()}
                        refreshList2={e => this.updateTable2(e)}
                        isAdmin={isAdmin}
                    >
                    </FileUpload>
                }


                <div style={{ "margin": "50px" }}>
                    <Table striped bordered hover responsive>
                        <thead>
                            <tr key={0}>
                                {
                                    isAdmin &&
                                    <th>User Name</th>
                                }

                                <th>File Name</th>
                                <th>Description</th>
                                <th>ID</th>
                                <th>File Upload Time</th>
                                <th>File Updated Time</th>
                                {!isAdmin && <th>Download</th>
                                }
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.userDataDynamo.map(item => {
                                    return (
                                        <tr key={item.id}>
                                            {
                                                isAdmin &&
                                                <td>{item.userName}</td>
                                            }

                                            <td>{item.fileName}</td>
                                            <td>{item.description}</td>
                                            <td>{item.id}</td>
                                            <td>{item.fileCreatedTime}</td>
                                            <td>{item.fileUpdatedTime}</td>
                                            { !isAdmin &&
                                            <td><Button variant="outline-success" onClick={event => this.onClickDownLoad(item.fileName)}>
                                                <a href={"https://dfbiaa0nywvvp.cloudfront.net/" + item.fileName} target="_blank" download={item.fileName}>DownLoad</a>
                                            </Button></td>
                                            }
                                            <td><Button variant="outline-danger" onClick={event => this.onClickDelete(item.fileName, item.id)}>
                                                Delete
                                            </Button></td>
                                        </tr>

                                    );
                                })
                            }

                        </tbody>
                    </Table>
                </div>

            </div>

        )
    }
}

export default UserPage
