import React, { PureComponent } from 'react'
import { Button, Navbar, Table } from "react-bootstrap";
import { dataService } from '../services/dataService';
import FileUpload from './FileUpload';
import LogOut from './LogOut';
import LogInPage from './LogInPage';
var jwt = require('jsonwebtoken');


class UserPage extends PureComponent {
    constructor(props) {
        super(props)

        const sessionToken = sessionStorage.getItem("token")
        // JWT DECODE 
        // Store decoded jwt in session storage

        this.state = {
            userDataDynamo: [],
            userData: undefined,
            desc: "",
            isAdmin: false
        }
        this.setDescription = this.setDescription.bind(this)
        this.updateTable = this.updateTable.bind(this)
    }

    setDescription(d) {
        this.setState({
            desc: d
        })
    }

    updateTable2=(user) =>{
        console.log("Called Update Table");
        dataService.getUserData(user)
            .then(json => {
                console.log(json);
                if (Array.isArray(json)) {
                    console.log("yes it is", json);
                    this.setState({
                        userDataDynamo: json
                    });

                }
            })
            .catch(reason => {
                console.log("Failed to fetch data from server, reason is : ", reason);
            });
    }
    updateTable() {
        console.log("Called Update Table");
        dataService.getAdminData()
            .then(json => {
                console.log(json);
                if (Array.isArray(json)) {
                    this.setState({
                        userDataDynamo: json
                    });
                }
            })
            .catch(reason => {
                console.log("Failed to fetch data from server, reason is : ", reason);
            });
    }
    componentDidMount() {

        var token = sessionStorage.getItem("token");
        var decoded = jwt.decode(token);
        // get the decoded payload and header
        var decoded = jwt.decode(token, { complete: true });
        console.log(decoded.header);
        console.log(decoded.payload);
        const userObj = decoded.payload;
        this.setState({
            userData: userObj
        })
        const isAdmin = userObj && userObj["cognito:groups"] && userObj["cognito:groups"].filter(g => g == "admin").length > 0;

        this.setState({ isAdmin });
        setTimeout(()=> {
            console.log("UserEmailCheckBefore:"+this.state.userData.email)
            console.log("isAdmin:"+this.state.isAdmin)
            if (this.state.isAdmin){
                this.updateTable()
            } else {
                this.updateTable2(this.state.userData.email);
            }
        }, 500);


        dataService.getUser()
    }


    onClickDownLoad(file) {
        window.open("https://d3k2ba7dd5osnx.cloudfront.net/" + file);
    }

    onClickDelete(fileName, id) {
        dataService.deleteFile(fileName, id)
            .then(json => {
                console.log(json);
                setTimeout(()=> {
                if (this.state.isAdmin){
                    this.updateTable()
                } else {
                    this.updateTable2(this.state.userData.email);
                }
            }, 300);

            })
            .catch(reason => {
                console.log("Failed to delete, reason is : ", reason);
            });
    }

    // fetchListAgain




    render() {

        const { isAdmin } = this.state;
        const tableData = [{"a":"apple","b":"ball"},{"a":"apple1","b":"ball1"}];
        const l = tableData.map((d) => <ul key={d.a}> <li>{d.a}</li> <li>{d.b}</li> </ul>);

        return(
            <div>
                {l}
            </div>
        );


    }
}

export default UserPage
